export function getQuestionBySlug(slug: string): Question | undefined {
  return sampleQuestions.find(question => question.slug === slug)
}

export function getAllQuestions(): Question[] {
  return sampleQuestions.filter(question => question.status === 'published')
}

export function getQuestionsByTag(tag: string): Question[] {
  return sampleQuestions.filter(question => 
    question.status === 'published' && question.tags.includes(tag)
  )
}

export function searchQuestions(query: string): Question[] {
  const lowercaseQuery = query.toLowerCase()
  return sampleQuestions.filter(question => 
    question.status === 'published' && (
      question.title.toLowerCase().includes(lowercaseQuery) ||
      question.question.toLowerCase().includes(lowercaseQuery) ||
      question.answer.toLowerCase().includes(lowercaseQuery) ||
      question.tags.some(tag => tag.toLowerCase().includes(lowercaseQuery))
    )
  )
}
